  "customer.loyalty_program_details_disclaimer": function(d) { return "Domino's Piece of the Pie Rewards<sup>&reg;</sup> program is open to Canadian residents 14 years and over with a Pizza Profile account who have registered for the program and who order online from participating Domino's locations only. Point redemption valid only for online orders from participating locations. See full terms and conditions here <a href=\"" + d.ctx + "/" + "#" + "!/content/terms/\" target=\"_blank\" class=\"js-loyaltyTermsBody\">Terms & Conditions</a>."; },
